import React from "react";
import "./Meme.css"


class Meme extends React.Component {
    render() {
        const scaleDown = {width: "50%", height: "auto"};
    const normalSize = {width: this.props.data.width, height: this.props.data.height};
    return(
        <div id = "memeWrapper">
            <img
                style = {this.props.data.width > 500 ? scaleDown : normalSize}
                alt = "Dank Meme"
                src = {this.props.data.img}
                onLoad = {() => this.props.handleLoad("hidden", "visible")}
            />

            <h2 className = "memeTopText"> {this.props.topText} </h2>

            <h2 className = "memeBottomText"> {this.props.bottomText} </h2>
        </div>
    );
    }
}

export default Meme;
