import React from "react";
import "./Meme.css"

function drawMeme(url) {
    let canvas = document.getElementById("memeCanvas");
    let ctx = canvas.getContext("2d");
    let image = new Image();
    image.onload = () => {
        ctx.drawImage(image, 0, 0, 300, 160);
    }
    image.src = url;
}

function Meme(props) {
    //drawMeme(props.url);

    return(
        <div className = "memeImage">
            <canvas id = "memeCanvas"></canvas>
            <h1>{drawMeme(props.url)}</h1>
        </div>
    );
}

export default Meme;
