import React from "react";
import domtoimage from 'dom-to-image';

import Form from "./Form";
import Meme from "./Meme";
import "./MemeGenerator.css";


class MemeGenerator extends React.Component {
    constructor() {
        super();

        this.state = {
            topText: "",
            bottomText: "",
            memeName: "Joker and Mini Joker",
            randomImg: "https://i.imgur.com/XTNyiVw.png",
            allMemeImgs: []
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleClick = this.handleClick.bind(this);
    }

    componentDidMount() {
        fetch("https://api.imgflip.com/get_memes")
        .then(response => response.json())
        .then(data => {
            this.setState({
                allMemeImgs: data.data.memes
            })
        })
        .catch(error => {
            console.log("Error while retrieving data from the server")
        })
    }

    shuffle() {
        let randomIndex = Math.floor(Math.random() * this.state.allMemeImgs.length);
        let meme = this.state.allMemeImgs[randomIndex];
        this.setState({
            memeName: meme.name,
            randomImg: meme.url
        });
    }

    save() {
        domtoimage.toJpeg(document.getElementsByClassName("memeImage"), {quality: 1.0})
        .then(dataUrl => {
            let link = document.createElement("a");
            link.download = this.state.memeName;
            link.href = dataUrl;
            link.click();
        });
    }

    handleClick(event) {
        //To prevent page from automatic refreshing
        event.preventDefault();
        const {name} = event.target;

        if(name === "shuffleButton") this.shuffle();
        else if(name === "saveButton") this.save();
    }

    handleChange(event) {
        const {name, value} = event.target;

        this.setState({
            [name]: value
        });
    }

    render() {
        return(
            <div className = "memeGeneratorBody">
                <Form 
                    topText = {this.state.topText}
                    bottomText = {this.state.bottomText}
                    handleChange = {this.handleChange}
                    handleClick = {this.handleClick}
                />

                <Meme 
                    url = {this.state.randomImg}
                    topText = {this.state.topText}
                    bottomText = {this.state.bottomText}
                />         

                <canvas id = "memeCanvas"></canvas>
            </div>
        );
    }
}


export default MemeGenerator;
