import React from "react";
import "./Meme.css"

function Meme(props) {
    const scaleDown = {width: "80%", height: "auto"};
    return(
        <div id = "memeWrapper">
            <img 
                style = {props.data.width > 500 && scaleDown}
                alt = "Dank Meme"
                src = {props.data.img}
            />

            <h2 className = "memeTopText"> {props.topText} </h2>

            <h2 className = "memeBottomText"> {props.bottomText} </h2>
        </div>
    );
}

export default Meme;
