import React from "react";
import "./Meme.css"

function Meme(props) {
    const vw = Math.max(document.documentElement.clientWidth, window.innerWidth || 0);
    let scaleDown;
    if(vw < 500) {
        scaleDown = {width: "50%", height: "auto"};
    }
    else {
        scaleDown = {width: "80%", height: "auto"};
    }

    const normalSize = {width: props.data.width, height: props.data.height};

    return(
        <div id = "memeWrapper">
            <img
                style = {props.data.width > 400 ? scaleDown : normalSize}
                alt = "Dank Meme"
                src = {props.data.img}
                onLoad = {() => props.handleLoad("hidden", "visible")}
            />

            <h2 className = "memeTopText">{props.topText}</h2>

            <h2 className = "memeBottomText">{props.bottomText}</h2>
        </div>
    );
}

export default Meme;
